import { useState } from "react";
import './index.css';


const Card = () => {
  const [count, setCount] = useState(0);

  function handleAdd() {
    setCount(count+1);
  }
  function handleSubstract() {
    setCount (count-1)
  }

  function handleReset() {
    setCount(0);
  }
    return (
        <div className="Card">
          <h1>React Counter App</h1>
          {count}
          <button className="btn-primary" onClick={handleSubstract}>- Substract</button>          
          <button className="btn-success" onClick={handleAdd}>+ Add</button>    
          <button className="btn-danger" onClick={handleReset}>Reset</button>    
        </div>

    )
    }

export default Card;
